mapper.update:
UPDATE BATCHJOBQUE
SET    QUE_STATUS		= #{QUE_STATUS}

      ,START_DT			= NOW()
      ,HOST				= #{HOSTNAME}


	  ,END_DT			= NOW()

WHERE  SEQ		 		= #{SEQ}

mapper.select:
SELECT 	A.CRAWLER_LOCATION
FROM 	CRAWLERINFO A
WHERE	A.HOSTNAME = #{HOSTNAME}
AND		A.CRAWLER_NAME = #{BATCHJOBID}
